*,
*::after,
*::before {
    margin: 0;
    padding: 0;
    box-sizing: inherit;
}

html {
    // Defines 1 rem
    font-size: 62.5%;

    @include respond(tab-land) {
        font-size: 56.25%; // 1rem = 9px
    }

    @include respond(tab-port) {
        font-size: 50%; // 1rem = 8px
    }

    @include respond(phone) {
        font-size: 37.5% // 1rem = 6px
    }

    @include respond(big-desktop) {
        font-size: 75%; // 1rem = 12px
    }

}

